mapper.select:
select 'true' as QUERYID,

from hospital


    and hospital_name like CONCAT("%",#{hospitalName},"%")


    and hospital_number = #{hospitalNumber}


    and mailing_address like CONCAT("%",#{mailingAddress},"%")


    and postal_code = #{postalCode}


    and affiliating_area like CONCAT("%",#{affiliatingArea},"%")


    and hospital_rank = #{hospitalRank}


    and number_bed = #{numberBed}


    and is_profit = #{isProfit}


    and dean_phone = #{deanPhone}


    and dean_fax = #{deanFax}


    and medicament_phone = #{medicamentPhone}


    and hospital_type = #{hospitalType}


    and last_drug_money = #{lastDrugMoney}


    and last_business_money = #{lastBusinessMoney}

  and del_id = 0

select 'true' as QUERYID,

from hospital
where hp_id = #{hpId,jdbcType=INTEGER}
