mapper.select:
select pd_id, purchase_id, pd.drug_id, pd.supplier_id, bid_price, deal_price, purchase_quantity, purchase_total_amount, purchase_state, pd.create_time, pd.modified_time, pd.del_id del_id, di.serial_number,di.common_name,di.commodity_name,di.dosage_form,di.speciflcation, dic.dictionaries_name company,di.unit_id,di.coefficient,dict.dictionaries_name purchase_state_name, di.production_name,dm.bidding_price,sup.supplier_name,dicti.dictionaries_name quality_level, dictio.dictionaries_name drug_categorys,di.drug_category from purchase_drug pd LEFT JOIN drug_message dm on dm.dm_id=pd.drug_id LEFT JOIN drug_items di on di.di_id = dm.drug_items_id LEFT JOIN dictionaries dic on dic.dic_id=di.unit_id LEFT JOIN supplier sup on sup.sp_id=pd.supplier_id LEFT JOIN dictionaries dict on dict.dic_id=pd.purchase_state LEFT JOIN dictionaries dicti on dicti.dic_id=dm.quality_level LEFT JOIN dictionaries dictio on dictio.dic_id=di.drug_category
and serial_number like CONCAT("%",#{serialNumber},"%")
and common_name like CONCAT("%",#{commonName},"%")
and dosage_form like CONCAT("%",#{dosageForm},"%")
and speciflcation like CONCAT("%",#{speciflcation},"%")
and unit_id =#{unitId}
and coefficient =#{coefficient}
and production_name like CONCAT("%",#{productionName},"%")
and commodity_name like CONCAT("%",#{commodityName},"%")
and drug_category =#{drugCategory}
and purchase_state =#{purchaseState}
and purchase_id=#{purchaseId} and pd.del_id=0

mapper.update:
update purchase_drug
set del_id = 1
where pd_id = #{pdId}

update purchase_drug
set supplier_id = #{supId}
where pd_id=#{pdId}
and drug_id=#{drugId}
and del_id=0
