mapper.select:
select 'true' as QUERYID,

from storage
where st_id = #{stId,jdbcType=INTEGER}

update.foreach:
update storage

    purchase_order_id = #{param.purchaseOrderId},
    storage_volume = #{param.storageVolume},
    invoice = #{param.invoice},
    drug_batch_number = #{param.drugBatchNumber}


    st_id = #{param.stId}
