mapper.select:
    SELECT di_id,common_name,dosage_form,speciflcation,
	unit_id,coefficient,dc.dictionaries_name as drug_category_name,dic.dictionaries_name as drug_state_name
	FROM drug_items di
	LEFT JOIN dictionaries dc on di.drug_category = dc.dic_id
	LEFT JOIN dictionaries dic on di.drug_state = dic.dic_id


        and di.di_id = #{diId}


        and di.common_name like CONCAT("%",#{commonName},"%")


        and di.dosage_form like CONCAT("%",#{dosageForm},"%")


        and di.speciflcation = #{speciflcation}


        and di.unit_id = #{unitId}


        and di.coefficient = {coefficient}


        and di.drug_category = #{drugCategoryName}


        and di.drug_state = #{drugStateName}

      and di.del_id = 0

SELECT di_id,common_name,dosage_form,speciflcation, unit_id,coefficient,dc.dic_id as drug_category_name,dic.dic_id as drug_state_name FROM drug_items di LEFT JOIN dictionaries dc on di.drug_category = dc.dic_id LEFT JOIN dictionaries dic on di.drug_state = dic.dic_id
and di.di_id = #{diId}
and di.del_id = 0

mapper.update:
update drug_items
set del_id=1
where di_id = #{diId,jdbcType=INTEGER}
